"use strict";

var buttonAnniversaryStart = document.querySelector('.jsButtonAnniversaryStart');
var buttonAnniversaryPost = document.querySelector('.jsButtonAnniversaryPost');
var buttonAnniversaryRecord = document.querySelector('.jsButtonAnniversaryRecord');
var buttonAnniversaryErase = document.querySelector('.jsButtonAnniversaryErase');
var listening = false;
var textBox = document.querySelector('.jsAnniversaryTextBox');
var sound = document.querySelector('.jsSound');
window.SpeechRecognition = window.webkitSpeechRecognition || window.SpeechRecognition;

if (typeof SpeechRecognition !== 'undefined') {
  var recognition = new SpeechRecognition();
  recognition.lang = "id-ID";

  recognition.onstart = function () {
    listening = true;
    $('.jsRecordElement').addClass('is-recording');
    $('.jsRecordHint').text('Silahkan Berbicara...');
  };

  recognition.onend = function () {
    listening = false;
    $('.jsRecordElement').removeClass('is-recording');
    $('.jsRecordHint').text('Klik tombol mic untuk mulai berbicara');
  };
} else {
  $('.jsRecordHint').text('Fitur Voice Recognition Hanya Tersedia di Browser Chrome dan Mozilla Versi Terbaru');
  $('.jsButtonAnniversaryRecord').hide();
}

buttonAnniversaryRecord.addEventListener('click', function () {
  sound.play();
  dictate();
});
buttonAnniversaryErase.addEventListener('click', function () {
  $('.jsAnniversaryTextBox').html('');
});
buttonAnniversaryStart.addEventListener('click', function () {
  openModal('jsModalAnniversaryPosting');
});
buttonAnniversaryPost.addEventListener('click', function () {
  openModal('jsModalAnniversaryFinish');
});

var dictate = function dictate() {
  recognition.start();

  recognition.onresult = function (event) {
    var speechToText = event.results[0][0].transcript;
    textBox.innerHTML = speechToText;
  };
};

